void
calculadora_basica(char *host, float primernumero, float segundonumero, char operador)
{
	CLIENT *clnt;
	float  *result;


#ifndef	DEBUG
	clnt = clnt_create (host, CALCULADORA, CALCULADORA_SIMPLE, "udp");
	if (clnt == NULL) {
		clnt_pcreateerror (host);
		exit (1);
	}
#endif	/* DEBUG */

	switch (operador){
		case '+':
			result = sumar_1(primernumero,segundonumero, clnt);
			break;
		case '-':
			result = restar_1(primernumero,segundonumero,clnt);
			break;
		case 'x':
			result = multiplicar_1(primernumero,segundonumero,clnt);
			break;
		case '/':
			result = dividir_1(primernumero,segundonumero,clnt);
			break;
		default:
            printf("Operador no v치lido.\n");
            result = NULL;
            break;
	}

	if(result == (float *)NULL){
		clnt_perror(clnt, "call failed");
		exit(1);
	}

	printf("EL RESULTADO CALCULADO ES: %f\n", *result);

#ifndef	DEBUG
	clnt_destroy (clnt);
#endif	 /* DEBUG */
}

int operador_valido(char operador, const char *operadores, int tam)
{
	int resultado = 0;

	for (int i = 0; i < tam && resultado == 0; i++)
	{
		if (operador == operadores[i])
			resultado = 1;
	}

	return resultado;
}


